import React, {useEffect, useState} from 'react';
import './App.css';
import Box from './Box'
import Dropdown from "./Dropdown";

interface Task {
    id: number;
    title: string;
    completed: boolean;
}

const App: React.FC = () => {
    const [tasks, setTasks] = useState<Task[]>([
        {id: 1, title: 'Publish a new blog', completed: true},
        {id: 2, title: 'Buy food for dinner', completed: false},
        {id: 3, title: 'Call Marie at 10.00 PM', completed: false},
        {id: 4, title: 'Write a react blog post', completed: false},
    ]);

    const [showTasks, setShowTasks] = useState<Task[]>([]);
    const [progress, setProgress] = useState<number>(0);
    const [selectedOption, setSelectedOption] = useState<string>("All");
    const [newTaskTitle, setNewTaskTitle] = useState("");
    const [selectedTaskId, setSelectedTaskId] = useState(0);
    const [editingTaskId, setEditingTaskId] = useState<number>(0);
    const [editingTitle, setEditingTitle] = useState("");

    const addTaskHandler = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        if (newTaskTitle === '') return;
        const newTask = {id: tasks.length + 2, title: newTaskTitle, completed: false};
        setTasks([...tasks, newTask]);
        setNewTaskTitle('');  // resetting the input box.
    };

    const changeStatusHandler = (taskId: number) => {
        const updatedTasks = tasks.map(task => {
            if (task.id === taskId) {
                return {...task, completed: !task.completed};
            }
            return task;
        });
        setTasks(updatedTasks);
    };

    const handleToggleOptions = (taskId: number) => {
        if (selectedTaskId === taskId) {
            setSelectedTaskId(0); // If the selected task is clicked again, hide the options
        } else {
            setSelectedTaskId(taskId); // Otherwise, show options for the clicked task
        }
    };

    const handleOptionSelection = (option: string) => {
        if (option === 'Edit') {
            const selectedTask = tasks.find(task => task.id === selectedTaskId);
            if (selectedTask) {
                setEditingTitle(selectedTask.title);
            }
            setEditingTaskId(selectedTaskId);
        } else if (option === 'Delete') {
            const filteredTasks = tasks.filter(task => task.id !== selectedTaskId);
            setTasks(filteredTasks);
        }
        setSelectedTaskId(0);
    };

    const saveTaskHandler = (event: React.FormEvent) => {
        event.preventDefault();
        setTasks(tasks.map(task => task.id === editingTaskId ? {...task, title: editingTitle} : task));
        setEditingTaskId(0);
        setEditingTitle("");
    };

    useEffect(() => {
        const completedTasks = tasks.filter(task => task.completed).length;
        setProgress(completedTasks)
        if (selectedOption === 'Done') {
            setShowTasks(tasks.filter(task => task.completed))
        } else if (selectedOption === 'Undone') {
            setShowTasks(tasks.filter(task => !task.completed))
        } else {
            setShowTasks(tasks)
        }
    }, [selectedOption, tasks])

    return (
        <div className="app">
            <Box className="progress-box">
                <h2>Progress</h2>
                <div className="progress-bar-max" style={{width: '450px'}}>
                    <div className="progress-bar"
                         style={{width: progress * 450 / tasks.length}}></div>
                </div>
                <p style={{color: '#e8e7e3'}}>{tasks.filter(task => task.completed).length} completed</p>
            </Box>
            <ul className="task-list"
                style={{width: '100px', alignItems: 'center', justifyContent: 'center', display: 'grid'}}>
                <div style={{display: "flex", position: "relative"}}>
                    <h2>Tasks</h2>
                    <Dropdown options={["All", "Done", "Undone"]} onSelect={setSelectedOption}></Dropdown>
                </div>
                {showTasks.map(task => (
                    <Box>
                        <li key={task.id} className={task.completed ? 'completed' : ''} style={{marginTop: '10px'}}>
                            {editingTaskId === task.id ? (
                                <form onSubmit={saveTaskHandler}>
                                    <input
                                        type="text"
                                        value={editingTitle}
                                        onChange={e => setEditingTitle(e.target.value)}
                                        style={{marginTop: '10px', border: 'None', backgroundColor: '#f5f5f5', outline: 'none', fontSize: '15px'}}
                                    />
                                    <button type="submit" /* Apply the desired button styling here */>
                                        Save
                                    </button>
                                </form>
                            ) : (
                                <li className={task.completed ? 'completed' : ''} style={{marginTop: '10px'}}>
                                    <input type="checkbox" checked={task.completed} onChange={() => {
                                        changeStatusHandler(task.id)
                                    }}/>
                                    {task.title}
                                    <button style={{
                                        border: "none",
                                        background: "transparent",
                                        fontSize: "1em",
                                        right: '0',
                                        position: 'absolute',
                                        marginTop: '10px',
                                        marginRight: '8px',
                                    }}
                                            onClick={() => handleToggleOptions(task.id)}>
                                        •••
                                    </button>
                                </li>
                            )}
                            {selectedTaskId === task.id &&
                                <div className="popup-menu" style={{
                                    right: '0'
                                }}>
                                    <ul>
                                        <li onClick={() => handleOptionSelection("Edit")}>Edit</li>
                                        <li onClick={() => handleOptionSelection("Delete")}>Delete</li>
                                    </ul>
                                </div>}
                            </li>
                    </Box>
                    ))}
                <Box>
                    <form onSubmit={addTaskHandler}>
                        <input
                            type="text"
                            value={newTaskTitle}
                            onChange={(event) => setNewTaskTitle(event.target.value)}
                            placeholder="Add new task"
                            style={{border: 'none', backgroundColor: '#f5f5f5', outline: 'none'}}
                        />
                    </form>
                </Box>
            </ul>
        </div>
    );
}

export default App;
